mapper.sql:
WHERE w.writer = m.id



			AND title LIKE #{keyword}


			AND writer LIKE #{keyword}


			AND content LIKE #{keyword}



		AND (writer LIKE #{keyword}
			 OR title LIKE #{keyword}
			 OR content LIKE #{keyword})

mapper.update:
UPDATE tbl_sa_work SET
	viewcnt = viewcnt + 1
WHERE wno = #{wno}

UPDATE tbl_sa_work
SET title = #{title},
	content = #{content},
	w_img = #{w_img}
WHERE wno = #{wno}

mapper.select:
SELECT fullname FROM tbl_sa_attach
WHERE wno = #{wno}
ORDER BY regdate DESC
