mapper.sql:
WHERE b.writer = m.id




			AND title LIKE #{keyword}


			AND writer LIKE #{keyword}


			AND content LIKE #{keyword}



		AND (writer LIKE #{keyword}
			 OR title LIKE #{keyword}
			 OR content LIKE #{keyword})

mapper.update:
UPDATE tbl_sa_board SET
	viewcnt = viewcnt + 1
WHERE bno = #{bno}

UPDATE tbl_sa_board
SET title = #{title},
	content = #{content}
WHERE bno = #{bno}
