mapper.select:
SELECT name FROM tbl_sa_member
WHERE id = #{id}
AND pw = #{pw}

mapper.update:
UPDATE tbl_sa_member
SET pw = #{pw},
	name = #{name},
	email = #{email},
	phone = #{phone}
WHERE id = #{id}

mapper.select:
SELECT COUNT(*) 
FROM tbl_sa_follow
WHERE followingId = #{followingId}
AND followerId = #{followerId}

mapper.delete:
DELETE FROM tbl_sa_follow
WHERE followingId = #{followingId}
AND followerId = #{followerId}

mapper.select:
SELECT COUNT(*)
FROM tbl_sa_follow
WHERE followingId = #{followingId}

SELECT COUNT(*)
FROM tbl_sa_follow
WHERE followerid = #{followingid}
